#!/bin/bash
# Uninstall script for Raspberry Pi Imager
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Raspberry Pi Imager..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Raspberry Pi Imager if running..."
pkill -f "Raspberry Pi Imager" 2>/dev/null || true

# Remove /Applications/Raspberry Pi Imager.app
echo "Removing /Applications/Raspberry Pi Imager.app..."
if [ -d "/Applications/Raspberry Pi Imager.app" ]; then
    rm -rf "/Applications/Raspberry Pi Imager.app" 2>/dev/null || true
elif [ -f "/Applications/Raspberry Pi Imager.app" ]; then
    rm -f "/Applications/Raspberry Pi Imager.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Raspberry Pi
echo "Removing $HOME/Library/Caches/Raspberry Pi..."
if [ -d "$HOME/Library/Caches/Raspberry Pi" ]; then
    rm -rf "$HOME/Library/Caches/Raspberry Pi" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Raspberry Pi" ]; then
    rm -f "$HOME/Library/Caches/Raspberry Pi" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.raspberrypi.Raspberry Pi Imager.plist
echo "Removing $HOME/Library/Preferences/com.raspberrypi.Raspberry Pi Imager.plist..."
if [ -d "$HOME/Library/Preferences/com.raspberrypi.Raspberry Pi Imager.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.raspberrypi.Raspberry Pi Imager.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.raspberrypi.Raspberry Pi Imager.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.raspberrypi.Raspberry Pi Imager.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.raspberrypi.imagingutility.savedState
echo "Removing $HOME/Library/Saved Application State/com.raspberrypi.imagingutility.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.raspberrypi.imagingutility.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.raspberrypi.imagingutility.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.raspberrypi.imagingutility.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.raspberrypi.imagingutility.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
