#!/bin/bash
# Uninstall script for RawTherapee
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling RawTherapee..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping RawTherapee if running..."
pkill -f "RawTherapee" 2>/dev/null || true

# Remove /Applications/RawTherapee.app
echo "Removing /Applications/RawTherapee.app..."
if [ -d "/Applications/RawTherapee.app" ]; then
    rm -rf "/Applications/RawTherapee.app" 2>/dev/null || true
elif [ -f "/Applications/RawTherapee.app" ]; then
    rm -f "/Applications/RawTherapee.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/RawTherapee
echo "Removing $HOME/Library/Application Support/RawTherapee..."
if [ -d "$HOME/Library/Application Support/RawTherapee" ]; then
    rm -rf "$HOME/Library/Application Support/RawTherapee" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/RawTherapee" ]; then
    rm -f "$HOME/Library/Application Support/RawTherapee" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
