# Remove /Applications/Recut.app
echo "Removing /Applications/Recut.app..."
if [ -d "/Applications/Recut.app" ]; then
    rm -rf "/Applications/Recut.app" 2>/dev/null || true
elif [ -f "/Applications/Recut.app" ]; then
    rm -f "/Applications/Recut.app" 2>/dev/null || true
fi

