#!/bin/bash
# Uninstall script for RemNote
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling RemNote..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping RemNote if running..."
pkill -f "RemNote" 2>/dev/null || true

# Remove /Applications/RemNote.app
echo "Removing /Applications/RemNote.app..."
if [ -d "/Applications/RemNote.app" ]; then
    rm -rf "/Applications/RemNote.app" 2>/dev/null || true
elif [ -f "/Applications/RemNote.app" ]; then
    rm -f "/Applications/RemNote.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/RemNote
echo "Removing $HOME/Library/Application Support/RemNote..."
if [ -d "$HOME/Library/Application Support/RemNote" ]; then
    rm -rf "$HOME/Library/Application Support/RemNote" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/RemNote" ]; then
    rm -f "$HOME/Library/Application Support/RemNote" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/io.remnote.plist
echo "Removing $HOME/Library/Preferences/io.remnote.plist..."
if [ -d "$HOME/Library/Preferences/io.remnote.plist" ]; then
    rm -rf "$HOME/Library/Preferences/io.remnote.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/io.remnote.plist" ]; then
    rm -f "$HOME/Library/Preferences/io.remnote.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/io.remnote.savedState
echo "Removing $HOME/Library/Saved Application State/io.remnote.savedState..."
if [ -d "$HOME/Library/Saved Application State/io.remnote.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/io.remnote.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/io.remnote.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/io.remnote.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
