#!/bin/bash
# Uninstall script for Responsively
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Responsively..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Responsively if running..."
pkill -f "Responsively" 2>/dev/null || true

# Remove /Applications/ResponsivelyApp.app
echo "Removing /Applications/ResponsivelyApp.app..."
if [ -d "/Applications/ResponsivelyApp.app" ]; then
    rm -rf "/Applications/ResponsivelyApp.app" 2>/dev/null || true
elif [ -f "/Applications/ResponsivelyApp.app" ]; then
    rm -f "/Applications/ResponsivelyApp.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/ResponsivelyApp
echo "Removing $HOME/Library/Application Support/ResponsivelyApp..."
if [ -d "$HOME/Library/Application Support/ResponsivelyApp" ]; then
    rm -rf "$HOME/Library/Application Support/ResponsivelyApp" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/ResponsivelyApp" ]; then
    rm -f "$HOME/Library/Application Support/ResponsivelyApp" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/app.responsively.plist
echo "Removing $HOME/Library/Preferences/app.responsively.plist..."
if [ -d "$HOME/Library/Preferences/app.responsively.plist" ]; then
    rm -rf "$HOME/Library/Preferences/app.responsively.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/app.responsively.plist" ]; then
    rm -f "$HOME/Library/Preferences/app.responsively.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
