#!/bin/bash
# Uninstall script for RetroArch
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling RetroArch..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping RetroArch if running..."
pkill -f "RetroArch" 2>/dev/null || true

# Remove /Applications/RetroArch.app
echo "Removing /Applications/RetroArch.app..."
if [ -d "/Applications/RetroArch.app" ]; then
    rm -rf "/Applications/RetroArch.app" 2>/dev/null || true
elif [ -f "/Applications/RetroArch.app" ]; then
    rm -f "/Applications/RetroArch.app" 2>/dev/null || true
fi

# Remove $HOME/Documents/RetroArch/
echo "Removing $HOME/Documents/RetroArch/..."
if [ -d "$HOME/Documents/RetroArch/" ]; then
    rm -rf "$HOME/Documents/RetroArch/" 2>/dev/null || true
elif [ -f "$HOME/Documents/RetroArch/" ]; then
    rm -f "$HOME/Documents/RetroArch/" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/RetroArch
echo "Removing $HOME/Library/Application Support/RetroArch..."
if [ -d "$HOME/Library/Application Support/RetroArch" ]; then
    rm -rf "$HOME/Library/Application Support/RetroArch" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/RetroArch" ]; then
    rm -f "$HOME/Library/Application Support/RetroArch" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.libretro.RetroArch.savedState
echo "Removing $HOME/Library/Saved Application State/com.libretro.RetroArch.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.libretro.RetroArch.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.libretro.RetroArch.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.libretro.RetroArch.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.libretro.RetroArch.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
