#!/bin/bash
# Uninstall script for Riverside Studio
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Riverside Studio..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Riverside Studio if running..."
pkill -f "Riverside Studio" 2>/dev/null || true

# Remove /Applications/Riverside Studio.app
echo "Removing /Applications/Riverside Studio.app..."
if [ -d "/Applications/Riverside Studio.app" ]; then
    rm -rf "/Applications/Riverside Studio.app" 2>/dev/null || true
elif [ -f "/Applications/Riverside Studio.app" ]; then
    rm -f "/Applications/Riverside Studio.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/RVS-Riverside.fm-Mac
echo "Removing $HOME/Library/Application Scripts/RVS-Riverside.fm-Mac..."
if [ -d "$HOME/Library/Application Scripts/RVS-Riverside.fm-Mac" ]; then
    rm -rf "$HOME/Library/Application Scripts/RVS-Riverside.fm-Mac" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/RVS-Riverside.fm-Mac" ]; then
    rm -f "$HOME/Library/Application Scripts/RVS-Riverside.fm-Mac" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/RVS-Riverside.fm-Mac
echo "Removing $HOME/Library/Containers/RVS-Riverside.fm-Mac..."
if [ -d "$HOME/Library/Containers/RVS-Riverside.fm-Mac" ]; then
    rm -rf "$HOME/Library/Containers/RVS-Riverside.fm-Mac" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/RVS-Riverside.fm-Mac" ]; then
    rm -f "$HOME/Library/Containers/RVS-Riverside.fm-Mac" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
