#!/bin/bash
# Uninstall script for Roam Research
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Roam Research..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Roam Research if running..."
pkill -f "Roam Research" 2>/dev/null || true

# Remove /Applications/Roam Research.app
echo "Removing /Applications/Roam Research.app..."
if [ -d "/Applications/Roam Research.app" ]; then
    rm -rf "/Applications/Roam Research.app" 2>/dev/null || true
elif [ -f "/Applications/Roam Research.app" ]; then
    rm -f "/Applications/Roam Research.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.roam-research.desktop-app.plist
echo "Removing $HOME/Library/Preferences/com.roam-research.desktop-app.plist..."
if [ -d "$HOME/Library/Preferences/com.roam-research.desktop-app.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.roam-research.desktop-app.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.roam-research.desktop-app.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.roam-research.desktop-app.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.roam-research.desktop-app.savedState
echo "Removing $HOME/Library/Saved Application State/com.roam-research.desktop-app.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.roam-research.desktop-app.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.roam-research.desktop-app.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.roam-research.desktop-app.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.roam-research.desktop-app.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
