#!/bin/bash
# Uninstall script for Rocketman Choices Packager
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Rocketman Choices Packager..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Rocketman Choices Packager if running..."
pkill -f "Rocketman Choices Packager" 2>/dev/null || true

# Remove $HOME/Library/Preferences/tech.rocketman.ChoicesPackager.plist
echo "Removing $HOME/Library/Preferences/tech.rocketman.ChoicesPackager.plist..."
if [ -d "$HOME/Library/Preferences/tech.rocketman.ChoicesPackager.plist" ]; then
    rm -rf "$HOME/Library/Preferences/tech.rocketman.ChoicesPackager.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/tech.rocketman.ChoicesPackager.plist" ]; then
    rm -f "$HOME/Library/Preferences/tech.rocketman.ChoicesPackager.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
