fi

# Remove $HOME/Library/Saved Application State/com.roon.Roon.savedState
echo "Removing $HOME/Library/Saved Application State/com.roon.Roon.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.roon.Roon.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.roon.Roon.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.roon.Roon.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.roon.Roon.savedState" 2>/dev/null || true
fi

