#!/bin/bash
# Uninstall script for Salesforce CLI
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Salesforce CLI..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Salesforce CLI if running..."
pkill -f "Salesforce CLI" 2>/dev/null || true

# Remove $HOME/.cache/sf
echo "Removing $HOME/.cache/sf..."
if [ -d "$HOME/.cache/sf" ]; then
    rm -rf "$HOME/.cache/sf" 2>/dev/null || true
elif [ -f "$HOME/.cache/sf" ]; then
    rm -f "$HOME/.cache/sf" 2>/dev/null || true
fi

# Remove $HOME/.config/sf
echo "Removing $HOME/.config/sf..."
if [ -d "$HOME/.config/sf" ]; then
    rm -rf "$HOME/.config/sf" 2>/dev/null || true
elif [ -f "$HOME/.config/sf" ]; then
    rm -f "$HOME/.config/sf" 2>/dev/null || true
fi

# Remove $HOME/.local/share/sf
echo "Removing $HOME/.local/share/sf..."
if [ -d "$HOME/.local/share/sf" ]; then
    rm -rf "$HOME/.local/share/sf" 2>/dev/null || true
elif [ -f "$HOME/.local/share/sf" ]; then
    rm -f "$HOME/.local/share/sf" 2>/dev/null || true
fi

# Remove $HOME/.sf
echo "Removing $HOME/.sf..."
if [ -d "$HOME/.sf" ]; then
    rm -rf "$HOME/.sf" 2>/dev/null || true
elif [ -f "$HOME/.sf" ]; then
    rm -f "$HOME/.sf" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
