echo "Removing /Applications/SC Menu.app..."
if [ -d "/Applications/SC Menu.app" ]; then
    rm -rf "/Applications/SC Menu.app" 2>/dev/null || true
elif [ -f "/Applications/SC Menu.app" ]; then
    rm -f "/Applications/SC Menu.app" 2>/dev/null || true
fi

