#!/bin/bash
# Uninstall script for Screaming Frog SEO Spider
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Screaming Frog SEO Spider..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Screaming Frog SEO Spider if running..."
pkill -f "Screaming Frog SEO Spider" 2>/dev/null || true

# Remove /Applications/Screaming Frog SEO Spider.app
echo "Removing /Applications/Screaming Frog SEO Spider.app..."
if [ -d "/Applications/Screaming Frog SEO Spider.app" ]; then
    rm -rf "/Applications/Screaming Frog SEO Spider.app" 2>/dev/null || true
elif [ -f "/Applications/Screaming Frog SEO Spider.app" ]; then
    rm -f "/Applications/Screaming Frog SEO Spider.app" 2>/dev/null || true
fi

# Remove $HOME/.ScreamingFrogSEOSpider
echo "Removing $HOME/.ScreamingFrogSEOSpider..."
if [ -d "$HOME/.ScreamingFrogSEOSpider" ]; then
    rm -rf "$HOME/.ScreamingFrogSEOSpider" 2>/dev/null || true
elif [ -f "$HOME/.ScreamingFrogSEOSpider" ]; then
    rm -f "$HOME/.ScreamingFrogSEOSpider" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/uk.co.screamingfrog.seospider.ui.b
echo "Removing $HOME/Library/Application Support/uk.co.screamingfrog.seospider.ui.b..."
if [ -d "$HOME/Library/Application Support/uk.co.screamingfrog.seospider.ui.b" ]; then
    rm -rf "$HOME/Library/Application Support/uk.co.screamingfrog.seospider.ui.b" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/uk.co.screamingfrog.seospider.ui.b" ]; then
