#!/bin/bash
# Uninstall script for Scribus
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Scribus..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Scribus if running..."
pkill -f "Scribus" 2>/dev/null || true

# Remove /Applications/Scribus.app
echo "Removing /Applications/Scribus.app..."
if [ -d "/Applications/Scribus.app" ]; then
    rm -rf "/Applications/Scribus.app" 2>/dev/null || true
elif [ -f "/Applications/Scribus.app" ]; then
    rm -f "/Applications/Scribus.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Scribus
echo "Removing $HOME/Library/Application Support/Scribus..."
if [ -d "$HOME/Library/Application Support/Scribus" ]; then
    rm -rf "$HOME/Library/Application Support/Scribus" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Scribus" ]; then
    rm -f "$HOME/Library/Application Support/Scribus" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/Scribus
echo "Removing $HOME/Library/Preferences/Scribus..."
if [ -d "$HOME/Library/Preferences/Scribus" ]; then
    rm -rf "$HOME/Library/Preferences/Scribus" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/Scribus" ]; then
    rm -f "$HOME/Library/Preferences/Scribus" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/net.scribus.savedState
echo "Removing $HOME/Library/Saved Application State/net.scribus.savedState..."
if [ -d "$HOME/Library/Saved Application State/net.scribus.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/net.scribus.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/net.scribus.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/net.scribus.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
