#!/bin/bash
# Uninstall script for Scrivener
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Scrivener..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Scrivener if running..."
pkill -f "Scrivener" 2>/dev/null || true

# Remove /Applications/Scrivener.app
echo "Removing /Applications/Scrivener.app..."
if [ -d "/Applications/Scrivener.app" ]; then
    rm -rf "/Applications/Scrivener.app" 2>/dev/null || true
elif [ -f "/Applications/Scrivener.app" ]; then
    rm -f "/Applications/Scrivener.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Scrivener
echo "Removing $HOME/Library/Application Support/Scrivener..."
if [ -d "$HOME/Library/Application Support/Scrivener" ]; then
    rm -rf "$HOME/Library/Application Support/Scrivener" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Scrivener" ]; then
    rm -f "$HOME/Library/Application Support/Scrivener" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.literatureandlatte.scrivener*
echo "Removing $HOME/Library/Caches/com.literatureandlatte.scrivener*..."
if [ -d "$HOME/Library/Caches/com.literatureandlatte.scrivener*" ]; then
    rm -rf "$HOME/Library/Caches/com.literatureandlatte.scrivener*" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.literatureandlatte.scrivener*" ]; then
    rm -f "$HOME/Library/Caches/com.literatureandlatte.scrivener*" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.literatureandlatte.scrivener*.plist
echo "Removing $HOME/Library/Preferences/com.literatureandlatte.scrivener*.plist..."
if [ -d "$HOME/Library/Preferences/com.literatureandlatte.scrivener*.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.literatureandlatte.scrivener*.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.literatureandlatte.scrivener*.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.literatureandlatte.scrivener*.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
