#!/bin/bash
# Uninstall script for Scroll Reverser
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Scroll Reverser..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Scroll Reverser if running..."
pkill -f "Scroll Reverser" 2>/dev/null || true

# Remove /Applications/Scroll Reverser.app
echo "Removing /Applications/Scroll Reverser.app..."
if [ -d "/Applications/Scroll Reverser.app" ]; then
    rm -rf "/Applications/Scroll Reverser.app" 2>/dev/null || true
elif [ -f "/Applications/Scroll Reverser.app" ]; then
    rm -f "/Applications/Scroll Reverser.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.pilotmoon.scroll-reverser
echo "Removing $HOME/Library/Caches/com.pilotmoon.scroll-reverser..."
if [ -d "$HOME/Library/Caches/com.pilotmoon.scroll-reverser" ]; then
    rm -rf "$HOME/Library/Caches/com.pilotmoon.scroll-reverser" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.pilotmoon.scroll-reverser" ]; then
    rm -f "$HOME/Library/Caches/com.pilotmoon.scroll-reverser" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.pilotmoon.scroll-reverser.plist
echo "Removing $HOME/Library/Preferences/com.pilotmoon.scroll-reverser.plist..."
if [ -d "$HOME/Library/Preferences/com.pilotmoon.scroll-reverser.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.pilotmoon.scroll-reverser.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.pilotmoon.scroll-reverser.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.pilotmoon.scroll-reverser.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
