#!/bin/bash
# Uninstall script for SelfControl
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SelfControl..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SelfControl if running..."
pkill -f "SelfControl" 2>/dev/null || true

# Remove /Applications/SelfControl.app
echo "Removing /Applications/SelfControl.app..."
if [ -d "/Applications/SelfControl.app" ]; then
    rm -rf "/Applications/SelfControl.app" 2>/dev/null || true
elif [ -f "/Applications/SelfControl.app" ]; then
    rm -f "/Applications/SelfControl.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.eyebeam.SelfControl.plist
echo "Removing $HOME/Library/Preferences/org.eyebeam.SelfControl.plist..."
if [ -d "$HOME/Library/Preferences/org.eyebeam.SelfControl.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.eyebeam.SelfControl.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.eyebeam.SelfControl.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.eyebeam.SelfControl.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
