#!/bin/bash
# Uninstall script for Send to Kindle
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Send to Kindle..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Send to Kindle if running..."
pkill -f "Send to Kindle" 2>/dev/null || true

# Unload service com.amazon.sendtokindle.launcher
echo "Unloading service com.amazon.sendtokindle.launcher..."
launchctl unload -w /Library/LaunchAgents/com.amazon.sendtokindle.launcher.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.amazon.sendtokindle.launcher.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.amazon.sendtokindle.launcher.plist 2>/dev/null || true

# Kill application with bundle ID com.amazon.SendToKindle if running
echo "Stopping application with bundle ID com.amazon.SendToKindle if running..."
killall -9 "com.amazon.SendToKindle" 2>/dev/null || true

# Remove $HOME/Library/Application Support/Amazon/SendToKindle
echo "Removing $HOME/Library/Application Support/Amazon/SendToKindle..."
if [ -d "$HOME/Library/Application Support/Amazon/SendToKindle" ]; then
    rm -rf "$HOME/Library/Application Support/Amazon/SendToKindle" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Amazon/SendToKindle" ]; then
    rm -f "$HOME/Library/Application Support/Amazon/SendToKindle" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/SendToKindleInstall.log
echo "Removing $HOME/Library/Logs/SendToKindleInstall.log..."
if [ -d "$HOME/Library/Logs/SendToKindleInstall.log" ]; then
    rm -rf "$HOME/Library/Logs/SendToKindleInstall.log" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/SendToKindleInstall.log" ]; then
    rm -f "$HOME/Library/Logs/SendToKindleInstall.log" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.amazon.SendToKindle.plist
echo "Removing $HOME/Library/Preferences/com.amazon.SendToKindle.plist..."
if [ -d "$HOME/Library/Preferences/com.amazon.SendToKindle.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.amazon.SendToKindle.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.amazon.SendToKindle.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.amazon.SendToKindle.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
