#!/bin/bash
# Uninstall script for SF Symbols
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SF Symbols..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SF Symbols if running..."
pkill -f "SF Symbols" 2>/dev/null || true

# Remove $HOME/Library/Preferences/com.apple.SFSymbols.plist
echo "Removing $HOME/Library/Preferences/com.apple.SFSymbols.plist..."
if [ -d "$HOME/Library/Preferences/com.apple.SFSymbols.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.apple.SFSymbols.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.apple.SFSymbols.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.apple.SFSymbols.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
