#!/bin/bash
# Uninstall script for Shotcut
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Shotcut..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Shotcut if running..."
pkill -f "Shotcut" 2>/dev/null || true

# Remove /Applications/Shotcut.app
echo "Removing /Applications/Shotcut.app..."
if [ -d "/Applications/Shotcut.app" ]; then
    rm -rf "/Applications/Shotcut.app" 2>/dev/null || true
elif [ -f "/Applications/Shotcut.app" ]; then
    rm -f "/Applications/Shotcut.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Meltytech
echo "Removing $HOME/Library/Application Support/Meltytech..."
if [ -d "$HOME/Library/Application Support/Meltytech" ]; then
    rm -rf "$HOME/Library/Application Support/Meltytech" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Meltytech" ]; then
    rm -f "$HOME/Library/Application Support/Meltytech" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Meltytech
echo "Removing $HOME/Library/Caches/Meltytech..."
if [ -d "$HOME/Library/Caches/Meltytech" ]; then
    rm -rf "$HOME/Library/Caches/Meltytech" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Meltytech" ]; then
    rm -f "$HOME/Library/Caches/Meltytech" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.meltytech.Shotcut.plist
echo "Removing $HOME/Library/Preferences/com.meltytech.Shotcut.plist..."
if [ -d "$HOME/Library/Preferences/com.meltytech.Shotcut.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.meltytech.Shotcut.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.meltytech.Shotcut.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.meltytech.Shotcut.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
