#!/bin/bash
# Uninstall script for ShurePlus MOTIV
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling ShurePlus MOTIV..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping ShurePlus MOTIV if running..."
pkill -f "ShurePlus MOTIV" 2>/dev/null || true

# Kill application with bundle ID com.shure.motiv.desktop if running
echo "Stopping application with bundle ID com.shure.motiv.desktop if running..."
killall -9 "com.shure.motiv.desktop" 2>/dev/null || true

# Remove /Applications/ShurePlus MOTIV.app
echo "Removing /Applications/ShurePlus MOTIV.app..."
if [ -d "/Applications/ShurePlus MOTIV.app" ]; then
    rm -rf "/Applications/ShurePlus MOTIV.app" 2>/dev/null || true
elif [ -f "/Applications/ShurePlus MOTIV.app" ]; then
    rm -f "/Applications/ShurePlus MOTIV.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/ShurePlus MOTIV
echo "Removing $HOME/Library/Application Support/ShurePlus MOTIV..."
if [ -d "$HOME/Library/Application Support/ShurePlus MOTIV" ]; then
    rm -rf "$HOME/Library/Application Support/ShurePlus MOTIV" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/ShurePlus MOTIV" ]; then
    rm -f "$HOME/Library/Application Support/ShurePlus MOTIV" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/ShurePlus MOTIV
echo "Removing $HOME/Library/Logs/ShurePlus MOTIV..."
if [ -d "$HOME/Library/Logs/ShurePlus MOTIV" ]; then
