#!/bin/bash
# Uninstall script for Shutter Encoder
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Shutter Encoder..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Shutter Encoder if running..."
pkill -f "Shutter Encoder" 2>/dev/null || true

# Unload service application.com.paulpacifico.shutterencoder.*
echo "Unloading service application.com.paulpacifico.shutterencoder.*..."
launchctl unload -w /Library/LaunchAgents/application.com.paulpacifico.shutterencoder.*.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/application.com.paulpacifico.shutterencoder.*.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/application.com.paulpacifico.shutterencoder.*.plist 2>/dev/null || true

# Kill application with bundle ID com.paulpacifico.shutterencoder if running
echo "Stopping application with bundle ID com.paulpacifico.shutterencoder if running..."
killall -9 "com.paulpacifico.shutterencoder" 2>/dev/null || true

# Remove $HOME/Documents/Shutter Encoder
echo "Removing $HOME/Documents/Shutter Encoder..."
if [ -d "$HOME/Documents/Shutter Encoder" ]; then
    rm -rf "$HOME/Documents/Shutter Encoder" 2>/dev/null || true
elif [ -f "$HOME/Documents/Shutter Encoder" ]; then
    rm -f "$HOME/Documents/Shutter Encoder" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
