#!/bin/bash
# Uninstall script for SigmaOS
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SigmaOS..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SigmaOS if running..."
pkill -f "SigmaOS" 2>/dev/null || true

# Remove /Applications/SigmaOS.app
echo "Removing /Applications/SigmaOS.app..."
if [ -d "/Applications/SigmaOS.app" ]; then
    rm -rf "/Applications/SigmaOS.app" 2>/dev/null || true
elif [ -f "/Applications/SigmaOS.app" ]; then
    rm -f "/Applications/SigmaOS.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.sigmaos.sigmaos.macos
echo "Removing $HOME/Library/Application Scripts/com.sigmaos.sigmaos.macos..."
if [ -d "$HOME/Library/Application Scripts/com.sigmaos.sigmaos.macos" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.sigmaos.sigmaos.macos" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.sigmaos.sigmaos.macos" ]; then
    rm -f "$HOME/Library/Application Scripts/com.sigmaos.sigmaos.macos" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.sigmaos.sigmaos.macos
echo "Removing $HOME/Library/Containers/com.sigmaos.sigmaos.macos..."
if [ -d "$HOME/Library/Containers/com.sigmaos.sigmaos.macos" ]; then
    rm -rf "$HOME/Library/Containers/com.sigmaos.sigmaos.macos" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.sigmaos.sigmaos.macos" ]; then
    rm -f "$HOME/Library/Containers/com.sigmaos.sigmaos.macos" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
