#!/bin/bash
# Uninstall script for Silhouette Studio
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Silhouette Studio..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Silhouette Studio if running..."
pkill -f "Silhouette Studio" 2>/dev/null || true

# Remove /Applications/Silhouette Studio.app
echo "Removing /Applications/Silhouette Studio.app..."
if [ -d "/Applications/Silhouette Studio.app" ]; then
    rm -rf "/Applications/Silhouette Studio.app" 2>/dev/null || true
elif [ -f "/Applications/Silhouette Studio.app" ]; then
    rm -f "/Applications/Silhouette Studio.app" 2>/dev/null || true
fi

# Remove /Library/Application Support/com.aspexsoftware.Silhouette_Studio*
echo "Removing /Library/Application Support/com.aspexsoftware.Silhouette_Studio*..."
if [ -d "/Library/Application Support/com.aspexsoftware.Silhouette_Studio*" ]; then
    rm -rf "/Library/Application Support/com.aspexsoftware.Silhouette_Studio*" 2>/dev/null || true
elif [ -f "/Library/Application Support/com.aspexsoftware.Silhouette_Studio*" ]; then
    rm -f "/Library/Application Support/com.aspexsoftware.Silhouette_Studio*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.aspexsoftware.Silhouette_Studio*
echo "Removing $HOME/Library/Application Support/com.aspexsoftware.Silhouette_Studio*..."
if [ -d "$HOME/Library/Application Support/com.aspexsoftware.Silhouette_Studio*" ]; then
    rm -rf "$HOME/Library/Application Support/com.aspexsoftware.Silhouette_Studio*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.aspexsoftware.Silhouette_Studio*" ]; then
