    rm -rf "$HOME/Library/Preferences/com.aspexsoftware.Silhouette_Studio*" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.aspexsoftware.Silhouette_Studio*" ]; then
    rm -f "$HOME/Library/Preferences/com.aspexsoftware.Silhouette_Studio*" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.silhouettesoftware*
echo "Removing $HOME/Library/Preferences/com.silhouettesoftware*..."
if [ -d "$HOME/Library/Preferences/com.silhouettesoftware*" ]; then
    rm -rf "$HOME/Library/Preferences/com.silhouettesoftware*" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.silhouettesoftware*" ]; then
