#!/bin/bash
# Uninstall script for Simple Comic
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Simple Comic..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Simple Comic if running..."
pkill -f "Simple Comic" 2>/dev/null || true

# Remove /Applications/Simple Comic.app
echo "Removing /Applications/Simple Comic.app..."
if [ -d "/Applications/Simple Comic.app" ]; then
    rm -rf "/Applications/Simple Comic.app" 2>/dev/null || true
elif [ -f "/Applications/Simple Comic.app" ]; then
    rm -f "/Applications/Simple Comic.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Simple Comic
echo "Removing $HOME/Library/Application Support/Simple Comic..."
if [ -d "$HOME/Library/Application Support/Simple Comic" ]; then
    rm -rf "$HOME/Library/Application Support/Simple Comic" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Simple Comic" ]; then
    rm -f "$HOME/Library/Application Support/Simple Comic" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
