#!/bin/bash
# Uninstall script for SimpleDEMViewer
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SimpleDEMViewer..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SimpleDEMViewer if running..."
pkill -f "SimpleDEMViewer" 2>/dev/null || true

# Kill application with bundle ID jp.jizoh.SDVr* if running
echo "Stopping application with bundle ID jp.jizoh.SDVr* if running..."
killall -9 "jp.jizoh.SDVr*" 2>/dev/null || true

# Remove /Applications/SimpleDEMViewer 8.5.2/SimpleDEMViewer.app
echo "Removing /Applications/SimpleDEMViewer 8.5.2/SimpleDEMViewer.app..."
if [ -d "/Applications/SimpleDEMViewer 8.5.2/SimpleDEMViewer.app" ]; then
    rm -rf "/Applications/SimpleDEMViewer 8.5.2/SimpleDEMViewer.app" 2>/dev/null || true
elif [ -f "/Applications/SimpleDEMViewer 8.5.2/SimpleDEMViewer.app" ]; then
    rm -f "/Applications/SimpleDEMViewer 8.5.2/SimpleDEMViewer.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/jp.jizoh.SDVr8.plist
echo "Removing $HOME/Library/Preferences/jp.jizoh.SDVr8.plist..."
if [ -d "$HOME/Library/Preferences/jp.jizoh.SDVr8.plist" ]; then
    rm -rf "$HOME/Library/Preferences/jp.jizoh.SDVr8.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/jp.jizoh.SDVr8.plist" ]; then
    rm -f "$HOME/Library/Preferences/jp.jizoh.SDVr8.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
