#!/bin/bash
# Uninstall script for SiriMote
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SiriMote..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SiriMote if running..."
pkill -f "SiriMote" 2>/dev/null || true

# Remove /Applications/SiriMote.app
echo "Removing /Applications/SiriMote.app..."
if [ -d "/Applications/SiriMote.app" ]; then
    rm -rf "/Applications/SiriMote.app" 2>/dev/null || true
elif [ -f "/Applications/SiriMote.app" ]; then
    rm -f "/Applications/SiriMote.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/at.EternalStorms.SiriMote-nonappstore.plist
echo "Removing $HOME/Library/Preferences/at.EternalStorms.SiriMote-nonappstore.plist..."
if [ -d "$HOME/Library/Preferences/at.EternalStorms.SiriMote-nonappstore.plist" ]; then
    rm -rf "$HOME/Library/Preferences/at.EternalStorms.SiriMote-nonappstore.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/at.EternalStorms.SiriMote-nonappstore.plist" ]; then
    rm -f "$HOME/Library/Preferences/at.EternalStorms.SiriMote-nonappstore.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
