#!/bin/bash
# Uninstall script for Slidepad
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Slidepad..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

