#!/bin/bash
# Uninstall script for Sloth
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Sloth..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Sloth if running..."
pkill -f "Sloth" 2>/dev/null || true

# Remove /Applications/Sloth.app
echo "Removing /Applications/Sloth.app..."
if [ -d "/Applications/Sloth.app" ]; then
    rm -rf "/Applications/Sloth.app" 2>/dev/null || true
elif [ -f "/Applications/Sloth.app" ]; then
    rm -f "/Applications/Sloth.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/org.sveinbjorn.Sloth
echo "Removing $HOME/Library/Caches/org.sveinbjorn.Sloth..."
if [ -d "$HOME/Library/Caches/org.sveinbjorn.Sloth" ]; then
    rm -rf "$HOME/Library/Caches/org.sveinbjorn.Sloth" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/org.sveinbjorn.Sloth" ]; then
    rm -f "$HOME/Library/Caches/org.sveinbjorn.Sloth" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.sveinbjorn.Sloth.plist
echo "Removing $HOME/Library/Preferences/org.sveinbjorn.Sloth.plist..."
if [ -d "$HOME/Library/Preferences/org.sveinbjorn.Sloth.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.sveinbjorn.Sloth.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.sveinbjorn.Sloth.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.sveinbjorn.Sloth.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.sveinbjorn.Sloth.savedState
echo "Removing $HOME/Library/Saved Application State/org.sveinbjorn.Sloth.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.sveinbjorn.Sloth.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.sveinbjorn.Sloth.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.sveinbjorn.Sloth.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.sveinbjorn.Sloth.savedState" 2>/dev/null || true
fi

