#!/bin/bash
# Uninstall script for Smartsheet
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Smartsheet..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Smartsheet if running..."
pkill -f "Smartsheet" 2>/dev/null || true

# Remove /Applications/Smartsheet.app
echo "Removing /Applications/Smartsheet.app..."
if [ -d "/Applications/Smartsheet.app" ]; then
    rm -rf "/Applications/Smartsheet.app" 2>/dev/null || true
elif [ -f "/Applications/Smartsheet.app" ]; then
    rm -f "/Applications/Smartsheet.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.smartsheet.desktopapp.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.smartsheet.desktopapp.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.smartsheet.desktopapp.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.smartsheet.desktopapp.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.smartsheet.desktopapp.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.smartsheet.desktopapp.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Smartsheet
echo "Removing $HOME/Library/Application Support/Smartsheet..."
if [ -d "$HOME/Library/Application Support/Smartsheet" ]; then
    rm -rf "$HOME/Library/Application Support/Smartsheet" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Smartsheet" ]; then
    rm -f "$HOME/Library/Application Support/Smartsheet" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.smartsheet.desktopapp.plist
echo "Removing $HOME/Library/Preferences/com.smartsheet.desktopapp.plist..."
if [ -d "$HOME/Library/Preferences/com.smartsheet.desktopapp.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.smartsheet.desktopapp.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.smartsheet.desktopapp.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.smartsheet.desktopapp.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
