#!/bin/bash
# Uninstall script for SmartSVN
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SmartSVN..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SmartSVN if running..."
pkill -f "SmartSVN" 2>/dev/null || true

# Remove /Applications/SmartSVN.app
echo "Removing /Applications/SmartSVN.app..."
if [ -d "/Applications/SmartSVN.app" ]; then
    rm -rf "/Applications/SmartSVN.app" 2>/dev/null || true
elif [ -f "/Applications/SmartSVN.app" ]; then
    rm -f "/Applications/SmartSVN.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.syntevo.smartsvn.plist
echo "Removing $HOME/Library/Preferences/com.syntevo.smartsvn.plist..."
if [ -d "$HOME/Library/Preferences/com.syntevo.smartsvn.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.syntevo.smartsvn.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.syntevo.smartsvn.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.syntevo.smartsvn.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/SmartSVN
echo "Removing $HOME/Library/Preferences/SmartSVN..."
if [ -d "$HOME/Library/Preferences/SmartSVN" ]; then
    rm -rf "$HOME/Library/Preferences/SmartSVN" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/SmartSVN" ]; then
