#!/bin/bash
# Uninstall script for SmoothScroll
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SmoothScroll..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SmoothScroll if running..."
pkill -f "SmoothScroll" 2>/dev/null || true

# Remove /Applications/SmoothScroll.app
echo "Removing /Applications/SmoothScroll.app..."
if [ -d "/Applications/SmoothScroll.app" ]; then
    rm -rf "/Applications/SmoothScroll.app" 2>/dev/null || true
elif [ -f "/Applications/SmoothScroll.app" ]; then
    rm -f "/Applications/SmoothScroll.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.galambalazs.SmoothScroll
