elif [ -f "$HOME/Library/Caches/com.galambalazs.SmoothScroll" ]; then
    rm -f "$HOME/Library/Caches/com.galambalazs.SmoothScroll" 2>/dev/null || true
fi

# Remove $HOME/Library/HTTPStorages/com.galambalazs.SmoothScroll
echo "Removing $HOME/Library/HTTPStorages/com.galambalazs.SmoothScroll..."
