#!/bin/bash
# Uninstall script for Smultron
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Smultron..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Smultron if running..."
pkill -f "Smultron" 2>/dev/null || true

# Remove /Applications/Smultron.app
echo "Removing /Applications/Smultron.app..."
if [ -d "/Applications/Smultron.app" ]; then
    rm -rf "/Applications/Smultron.app" 2>/dev/null || true
elif [ -f "/Applications/Smultron.app" ]; then
    rm -f "/Applications/Smultron.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.peterborgapps.Smultron14
echo "Removing $HOME/Library/Application Scripts/com.peterborgapps.Smultron14..."
if [ -d "$HOME/Library/Application Scripts/com.peterborgapps.Smultron14" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.peterborgapps.Smultron14" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.peterborgapps.Smultron14" ]; then
    rm -f "$HOME/Library/Application Scripts/com.peterborgapps.Smultron14" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.peterborgapps.Smultron14
echo "Removing $HOME/Library/Containers/com.peterborgapps.Smultron14..."
if [ -d "$HOME/Library/Containers/com.peterborgapps.Smultron14" ]; then
    rm -rf "$HOME/Library/Containers/com.peterborgapps.Smultron14" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.peterborgapps.Smultron14" ]; then
    rm -f "$HOME/Library/Containers/com.peterborgapps.Smultron14" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
