#!/bin/bash
# Uninstall script for Sparkle
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Sparkle..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Sparkle if running..."
pkill -f "Sparkle" 2>/dev/null || true

# Remove /Applications/Sparkle Test App.app
echo "Removing /Applications/Sparkle Test App.app..."
if [ -d "/Applications/Sparkle Test App.app" ]; then
    rm -rf "/Applications/Sparkle Test App.app" 2>/dev/null || true
elif [ -f "/Applications/Sparkle Test App.app" ]; then
    rm -f "/Applications/Sparkle Test App.app" 2>/dev/null || true
fi

# Remove /usr/local/bin/sparkle.app/Contents/MacOS/sparkle
echo "Removing /usr/local/bin/sparkle.app/Contents/MacOS/sparkle..."
