elif [ -f "/usr/local/bin/sparkle.app/Contents/MacOS/sparkle" ]; then
    rm -f "/usr/local/bin/sparkle.app/Contents/MacOS/sparkle" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/org.sparkle-project.Downloader
