#!/bin/bash
# Uninstall script for Spitfire Audio
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Spitfire Audio..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Spitfire Audio if running..."
pkill -f "Spitfire Audio" 2>/dev/null || true

# Remove /Applications/Spitfire Audio.app
echo "Removing /Applications/Spitfire Audio.app..."
if [ -d "/Applications/Spitfire Audio.app" ]; then
    rm -rf "/Applications/Spitfire Audio.app" 2>/dev/null || true
elif [ -f "/Applications/Spitfire Audio.app" ]; then
    rm -f "/Applications/Spitfire Audio.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.spitfireaudio.spitfireaudio
echo "Removing $HOME/Library/Caches/com.spitfireaudio.spitfireaudio..."
if [ -d "$HOME/Library/Caches/com.spitfireaudio.spitfireaudio" ]; then
    rm -rf "$HOME/Library/Caches/com.spitfireaudio.spitfireaudio" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.spitfireaudio.spitfireaudio" ]; then
    rm -f "$HOME/Library/Caches/com.spitfireaudio.spitfireaudio" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.spitfireaudio.spitfireaudio.plist
echo "Removing $HOME/Library/Preferences/com.spitfireaudio.spitfireaudio.plist..."
if [ -d "$HOME/Library/Preferences/com.spitfireaudio.spitfireaudio.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.spitfireaudio.spitfireaudio.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.spitfireaudio.spitfireaudio.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.spitfireaudio.spitfireaudio.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
