#!/bin/bash
# Uninstall script for Splashtop Streamer
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Splashtop Streamer..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Splashtop Streamer if running..."
pkill -f "Splashtop Streamer" 2>/dev/null || true

# Unload service com.splashtop.streamer
echo "Unloading service com.splashtop.streamer..."
launchctl unload -w /Library/LaunchAgents/com.splashtop.streamer.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.splashtop.streamer.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.splashtop.streamer.plist 2>/dev/null || true

# Unload service com.splashtop.streamer-daemon
echo "Unloading service com.splashtop.streamer-daemon..."
launchctl unload -w /Library/LaunchAgents/com.splashtop.streamer-daemon.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.splashtop.streamer-daemon.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.splashtop.streamer-daemon.plist 2>/dev/null || true

# Unload service com.splashtop.streamer-for-root
echo "Unloading service com.splashtop.streamer-for-root..."
launchctl unload -w /Library/LaunchAgents/com.splashtop.streamer-for-root.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.splashtop.streamer-for-root.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.splashtop.streamer-for-root.plist 2>/dev/null || true

# Unload service com.splashtop.streamer-for-user
echo "Unloading service com.splashtop.streamer-for-user..."
launchctl unload -w /Library/LaunchAgents/com.splashtop.streamer-for-user.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.splashtop.streamer-for-user.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.splashtop.streamer-for-user.plist 2>/dev/null || true

# Unload service com.splashtop.streamer-srioframebuffer
echo "Unloading service com.splashtop.streamer-srioframebuffer..."
launchctl unload -w /Library/LaunchAgents/com.splashtop.streamer-srioframebuffer.plist 2>/dev/null || true
launchctl unload -w /Library/LaunchDaemons/com.splashtop.streamer-srioframebuffer.plist 2>/dev/null || true
launchctl unload -w ~/Library/LaunchAgents/com.splashtop.streamer-srioframebuffer.plist 2>/dev/null || true

# Kill application with bundle ID com.splashtop.Splashtop-Streamer if running
echo "Stopping application with bundle ID com.splashtop.Splashtop-Streamer if running..."
killall -9 "com.splashtop.Splashtop-Streamer" 2>/dev/null || true

# Remove $HOME/Library/Application Support/Splashtop Streamer
echo "Removing $HOME/Library/Application Support/Splashtop Streamer..."
if [ -d "$HOME/Library/Application Support/Splashtop Streamer" ]; then
    rm -rf "$HOME/Library/Application Support/Splashtop Streamer" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Splashtop Streamer" ]; then
    rm -f "$HOME/Library/Application Support/Splashtop Streamer" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.splashtop.Splashtop-Streamer.plist
echo "Removing $HOME/Library/Preferences/com.splashtop.Splashtop-Streamer.plist..."
if [ -d "$HOME/Library/Preferences/com.splashtop.Splashtop-Streamer.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.splashtop.Splashtop-Streamer.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.splashtop.Splashtop-Streamer.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.splashtop.Splashtop-Streamer.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
