#!/bin/bash
# Uninstall script for Spyder
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Spyder..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Spyder if running..."
pkill -f "Spyder" 2>/dev/null || true

# Kill application with bundle ID org.spyder-ide.Spyder-6 if running
echo "Stopping application with bundle ID org.spyder-ide.Spyder-6 if running..."
killall -9 "org.spyder-ide.Spyder-6" 2>/dev/null || true

# Remove $HOME/.spyder-py3
echo "Removing $HOME/.spyder-py3..."
if [ -d "$HOME/.spyder-py3" ]; then
    rm -rf "$HOME/.spyder-py3" 2>/dev/null || true
elif [ -f "$HOME/.spyder-py3" ]; then
    rm -f "$HOME/.spyder-py3" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Spyder
echo "Removing $HOME/Library/Application Support/Spyder..."
if [ -d "$HOME/Library/Application Support/Spyder" ]; then
    rm -rf "$HOME/Library/Application Support/Spyder" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Spyder" ]; then
    rm -f "$HOME/Library/Application Support/Spyder" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Spyder
echo "Removing $HOME/Library/Caches/Spyder..."
if [ -d "$HOME/Library/Caches/Spyder" ]; then
    rm -rf "$HOME/Library/Caches/Spyder" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Spyder" ]; then
    rm -f "$HOME/Library/Caches/Spyder" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.spyder-ide.Spyder.savedState
echo "Removing $HOME/Library/Saved Application State/org.spyder-ide.Spyder.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.spyder-ide.Spyder.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.spyder-ide.Spyder.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.spyder-ide.Spyder.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.spyder-ide.Spyder.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
