#!/bin/bash
# Uninstall script for Sqlectron
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Sqlectron..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Sqlectron if running..."
pkill -f "Sqlectron" 2>/dev/null || true

# Remove /Applications/sqlectron.app
echo "Removing /Applications/sqlectron.app..."
if [ -d "/Applications/sqlectron.app" ]; then
    rm -rf "/Applications/sqlectron.app" 2>/dev/null || true
elif [ -f "/Applications/sqlectron.app" ]; then
    rm -f "/Applications/sqlectron.app" 2>/dev/null || true
fi

# Remove $HOME/.sqlectron.json
echo "Removing $HOME/.sqlectron.json..."
if [ -d "$HOME/.sqlectron.json" ]; then
    rm -rf "$HOME/.sqlectron.json" 2>/dev/null || true
elif [ -f "$HOME/.sqlectron.json" ]; then
    rm -f "$HOME/.sqlectron.json" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Sqlectron
echo "Removing $HOME/Library/Application Support/Sqlectron..."
if [ -d "$HOME/Library/Application Support/Sqlectron" ]; then
    rm -rf "$HOME/Library/Application Support/Sqlectron" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Sqlectron" ]; then
    rm -f "$HOME/Library/Application Support/Sqlectron" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
