#!/bin/bash
# Uninstall script for SSH Config Editor
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SSH Config Editor..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SSH Config Editor if running..."
pkill -f "SSH Config Editor" 2>/dev/null || true

# Remove /Applications/SSH Config Editor.app
echo "Removing /Applications/SSH Config Editor.app..."
if [ -d "/Applications/SSH Config Editor.app" ]; then
    rm -rf "/Applications/SSH Config Editor.app" 2>/dev/null || true
elif [ -f "/Applications/SSH Config Editor.app" ]; then
    rm -f "/Applications/SSH Config Editor.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/SSH Config Editor
echo "Removing $HOME/Library/Application Support/SSH Config Editor..."
if [ -d "$HOME/Library/Application Support/SSH Config Editor" ]; then
    rm -rf "$HOME/Library/Application Support/SSH Config Editor" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/SSH Config Editor" ]; then
    rm -f "$HOME/Library/Application Support/SSH Config Editor" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.hejki.osx.sshce.plist
echo "Removing $HOME/Library/Preferences/org.hejki.osx.sshce.plist..."
if [ -d "$HOME/Library/Preferences/org.hejki.osx.sshce.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.hejki.osx.sshce.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.hejki.osx.sshce.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.hejki.osx.sshce.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/org.hejki.osx.sshce.savedState
echo "Removing $HOME/Library/Saved Application State/org.hejki.osx.sshce.savedState..."
if [ -d "$HOME/Library/Saved Application State/org.hejki.osx.sshce.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/org.hejki.osx.sshce.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/org.hejki.osx.sshce.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/org.hejki.osx.sshce.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
