#!/bin/bash
# Uninstall script for Stellarium
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Stellarium..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Stellarium if running..."
pkill -f "Stellarium" 2>/dev/null || true

# Remove /Applications/Stellarium.app
echo "Removing /Applications/Stellarium.app..."
if [ -d "/Applications/Stellarium.app" ]; then
    rm -rf "/Applications/Stellarium.app" 2>/dev/null || true
elif [ -f "/Applications/Stellarium.app" ]; then
    rm -f "/Applications/Stellarium.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Stellarium
echo "Removing $HOME/Library/Application Support/Stellarium..."
if [ -d "$HOME/Library/Application Support/Stellarium" ]; then
    rm -rf "$HOME/Library/Application Support/Stellarium" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Stellarium" ]; then
    rm -f "$HOME/Library/Application Support/Stellarium" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/Stellarium
echo "Removing $HOME/Library/Preferences/Stellarium..."
if [ -d "$HOME/Library/Preferences/Stellarium" ]; then
    rm -rf "$HOME/Library/Preferences/Stellarium" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/Stellarium" ]; then
    rm -f "$HOME/Library/Preferences/Stellarium" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
