#!/bin/bash
# Uninstall script for Stillcolor
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Stillcolor..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Stillcolor if running..."
pkill -f "Stillcolor" 2>/dev/null || true

# Remove /Applications/Stillcolor.app
echo "Removing /Applications/Stillcolor.app..."
if [ -d "/Applications/Stillcolor.app" ]; then
    rm -rf "/Applications/Stillcolor.app" 2>/dev/null || true
elif [ -f "/Applications/Stillcolor.app" ]; then
    rm -f "/Applications/Stillcolor.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.makkuk.Stillcolor
echo "Removing $HOME/Library/Application Scripts/com.makkuk.Stillcolor..."
if [ -d "$HOME/Library/Application Scripts/com.makkuk.Stillcolor" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.makkuk.Stillcolor" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.makkuk.Stillcolor" ]; then
    rm -f "$HOME/Library/Application Scripts/com.makkuk.Stillcolor" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.makkuk.Stillcolor
echo "Removing $HOME/Library/Containers/com.makkuk.Stillcolor..."
if [ -d "$HOME/Library/Containers/com.makkuk.Stillcolor" ]; then
    rm -rf "$HOME/Library/Containers/com.makkuk.Stillcolor" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.makkuk.Stillcolor" ]; then
    rm -f "$HOME/Library/Containers/com.makkuk.Stillcolor" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
