#!/bin/bash
# Uninstall script for Stremio
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Stremio..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Stremio if running..."
