#!/bin/bash
# Uninstall script for SubEthaEdit
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SubEthaEdit..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SubEthaEdit if running..."
pkill -f "SubEthaEdit" 2>/dev/null || true

# Remove /Applications/SubEthaEdit.app
echo "Removing /Applications/SubEthaEdit.app..."
if [ -d "/Applications/SubEthaEdit.app" ]; then
    rm -rf "/Applications/SubEthaEdit.app" 2>/dev/null || true
elif [ -f "/Applications/SubEthaEdit.app" ]; then
    rm -f "/Applications/SubEthaEdit.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/de.codingmonkeys.SubEthaEdit.MacFULL
echo "Removing $HOME/Library/Application Scripts/de.codingmonkeys.SubEthaEdit.MacFULL..."
if [ -d "$HOME/Library/Application Scripts/de.codingmonkeys.SubEthaEdit.MacFULL" ]; then
    rm -rf "$HOME/Library/Application Scripts/de.codingmonkeys.SubEthaEdit.MacFULL" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/de.codingmonkeys.SubEthaEdit.MacFULL" ]; then
    rm -f "$HOME/Library/Application Scripts/de.codingmonkeys.SubEthaEdit.MacFULL" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/de.codingmonkeys.SubEthaEdit.MacFULL
echo "Removing $HOME/Library/Containers/de.codingmonkeys.SubEthaEdit.MacFULL..."
if [ -d "$HOME/Library/Containers/de.codingmonkeys.SubEthaEdit.MacFULL" ]; then
    rm -rf "$HOME/Library/Containers/de.codingmonkeys.SubEthaEdit.MacFULL" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/de.codingmonkeys.SubEthaEdit.MacFULL" ]; then
    rm -f "$HOME/Library/Containers/de.codingmonkeys.SubEthaEdit.MacFULL" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
