#!/bin/bash
# Uninstall script for Sunsama
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Sunsama..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Sunsama if running..."
pkill -f "Sunsama" 2>/dev/null || true

# Remove /Applications/Sunsama.app
echo "Removing /Applications/Sunsama.app..."
if [ -d "/Applications/Sunsama.app" ]; then
    rm -rf "/Applications/Sunsama.app" 2>/dev/null || true
elif [ -f "/Applications/Sunsama.app" ]; then
    rm -f "/Applications/Sunsama.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Sunsama
echo "Removing $HOME/Library/Application Support/Sunsama..."
if [ -d "$HOME/Library/Application Support/Sunsama" ]; then
    rm -rf "$HOME/Library/Application Support/Sunsama" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Sunsama" ]; then
    rm -f "$HOME/Library/Application Support/Sunsama" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Sunsama
echo "Removing $HOME/Library/Logs/Sunsama..."
if [ -d "$HOME/Library/Logs/Sunsama" ]; then
    rm -rf "$HOME/Library/Logs/Sunsama" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Sunsama" ]; then
    rm -f "$HOME/Library/Logs/Sunsama" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.sunsama.native-app.plist
echo "Removing $HOME/Library/Preferences/com.sunsama.native-app.plist..."
