fi

# Remove $HOME/Library/Saved Application State/com.sunsama.native-app.savedState
echo "Removing $HOME/Library/Saved Application State/com.sunsama.native-app.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.sunsama.native-app.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.sunsama.native-app.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.sunsama.native-app.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.sunsama.native-app.savedState" 2>/dev/null || true
