#!/bin/bash
# Uninstall script for SuperCollider
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SuperCollider..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SuperCollider if running..."
pkill -f "SuperCollider" 2>/dev/null || true

# Remove /Applications/SuperCollider.app
echo "Removing /Applications/SuperCollider.app..."
if [ -d "/Applications/SuperCollider.app" ]; then
    rm -rf "/Applications/SuperCollider.app" 2>/dev/null || true
elif [ -f "/Applications/SuperCollider.app" ]; then
    rm -f "/Applications/SuperCollider.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/SuperCollider
echo "Removing $HOME/Library/Application Support/SuperCollider..."
if [ -d "$HOME/Library/Application Support/SuperCollider" ]; then
    rm -rf "$HOME/Library/Application Support/SuperCollider" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/SuperCollider" ]; then
    rm -f "$HOME/Library/Application Support/SuperCollider" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/net.sourceforge.supercollider.plist
echo "Removing $HOME/Library/Preferences/net.sourceforge.supercollider.plist..."
if [ -d "$HOME/Library/Preferences/net.sourceforge.supercollider.plist" ]; then
    rm -rf "$HOME/Library/Preferences/net.sourceforge.supercollider.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/net.sourceforge.supercollider.plist" ]; then
    rm -f "$HOME/Library/Preferences/net.sourceforge.supercollider.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/net.sourceforge.supercollider.savedState
echo "Removing $HOME/Library/Saved Application State/net.sourceforge.supercollider.savedState..."
if [ -d "$HOME/Library/Saved Application State/net.sourceforge.supercollider.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/net.sourceforge.supercollider.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/net.sourceforge.supercollider.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/net.sourceforge.supercollider.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
