#!/bin/bash
# Uninstall script for Support Companion
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Support Companion..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Support Companion if running..."
pkill -f "Support Companion" 2>/dev/null || true

# Remove /Library/Application Support/SupportCompanion
echo "Removing /Library/Application Support/SupportCompanion..."
if [ -d "/Library/Application Support/SupportCompanion" ]; then
    rm -rf "/Library/Application Support/SupportCompanion" 2>/dev/null || true
elif [ -f "/Library/Application Support/SupportCompanion" ]; then
    rm -f "/Library/Application Support/SupportCompanion" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/SupportCompanion
echo "Removing $HOME/Library/Application Support/SupportCompanion..."
if [ -d "$HOME/Library/Application Support/SupportCompanion" ]; then
    rm -rf "$HOME/Library/Application Support/SupportCompanion" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/SupportCompanion" ]; then
    rm -f "$HOME/Library/Application Support/SupportCompanion" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.github.macadmins.SupportCompanion.plist
echo "Removing $HOME/Library/Preferences/com.github.macadmins.SupportCompanion.plist..."
if [ -d "$HOME/Library/Preferences/com.github.macadmins.SupportCompanion.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.github.macadmins.SupportCompanion.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.github.macadmins.SupportCompanion.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.github.macadmins.SupportCompanion.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
